INSERT INTO `blogs` VALUES (1, 'ThinkPHP5.1的数据库链接和增删改查', 11, '1', 'ThinkPHP5.1的数据库链接和增删改查', '<p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><strong style=\"margin: 0px; padding: 0px;\">一、数据库的链接方式</strong></p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\">&lt;?php<br/>namespace appindexcontroller;<br/>use thinkDb;</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\">class Demo<br/>{<br/><strong style=\"margin: 0px; padding: 0px;\">//1、全局配置 config/database.php配置</strong><br/>public function dbTest()<br/>{<br/>return Db::table(&#39;pzq_article&#39;)<br/>-&gt;where(&#39;id&#39;,&#39;29&#39;)<br/>-&gt;value(&#39;title&#39;);<br/>}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><strong style=\"margin: 0px; padding: 0px;\">//2、动态配置 thinkdbQuery.php中有一个方法connect()</strong><br/>public function dbTest2()<br/>{<br/>return Db::connect([<br/>&#39;type&#39;=&gt;&#39;mysql&#39;,<br/>&#39;hostname&#39;=&gt;&#39;localhost&#39;,<br/>&#39;database&#39;=&gt;&#39;top789&#39;,<br/>&#39;username&#39;=&gt;&#39;root&#39;,<br/>&#39;password&#39;=&gt;&#39;root&#39;,<br/>])<br/>-&gt;table(&#39;pzq_article&#39;)<br/>-&gt;where(&#39;id&#39;,&#39;76&#39;)<br/>-&gt;value(&#39;title&#39;);<br/>}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><strong style=\"margin: 0px; padding: 0px;\">//3、DSN连接</strong><br/>public function dbTest3()<br/>{<br/>$dsn = &#39;mysql://root:root@localhost:3306/top789#utf8&#39;;<br/>return Db::connect($dsn)<br/>-&gt;table(&#39;pzq_article&#39;)<br/>-&gt;where(&#39;id&#39;,&#39;88&#39;)<br/>-&gt;value(&#39;title&#39;);<br/>}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><strong style=\"margin: 0px; padding: 0px;\">//4、单条查旬</strong><br/>public function dbTest4()<br/>{<br/>$res = Db::table(&#39;pzq_article&#39;)<br/>-&gt;field([&#39;title&#39;=&gt;&#39;标题&#39;,&#39;id&#39;=&gt;&#39;编号&#39;])//可添加别名<br/>-&gt;where(&#39;id&#39;,&#39;=&#39;,20)//如果是等号，=可以省略<br/>-&gt;find();//如果是主键查询，可省略上面where,这行写-&gt;find(20);<br/>dump(is_null($res)?&#39;没有查到&#39;:$res);<br/>}<br/><strong style=\"margin: 0px; padding: 0px;\">//5、多条查旬</strong><br/>public function dbTest5()<br/>{<br/>$res = Db::table(&#39;pzq_article&#39;)<br/>-&gt;field([&#39;id&#39;,&#39;cat_id&#39;,&#39;title&#39;])<br/>-&gt;where([<br/>[&#39;id&#39;,&#39;&gt;&#39;,20],<br/>[&#39;cat_id&#39;,&#39;=&#39;,2],<br/>])//一个条件，直接用表达式-&gt;where(&#39;id&#39;,&#39;&gt;&#39;,20)。多个条件用数组<br/>-&gt;order(&#39;id desc&#39;)-&gt;limit(3)-&gt;select();<br/><br/>if(empty($res)){<br/>return &#39;没有查到&#39;;<br/>}else{<br/>dump($res);<br/>}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\">}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><strong style=\"margin: 0px; padding: 0px;\">//6、数据添加</strong><br/>public function dbTest6()<br/>{<br/>$data = [<br/>&#39;name&#39;=&gt;&#39;Sam2&#39;,<br/>&#39;age&#39;=&gt;&#39;29&#39;,<br/>&#39;posttime&#39;=&gt;time()<br/>];<br/>$dataall=[<br/>[&#39;name&#39;=&gt;&#39;Sam3&#39;,&#39;age&#39;=&gt;&#39;29&#39;,&#39;posttime&#39;=&gt;time()],<br/>[&#39;name&#39;=&gt;&#39;Sam4&#39;,&#39;age&#39;=&gt;&#39;30&#39;,&#39;posttime&#39;=&gt;time()],<br/>];<br/>//(1)单条插入<br/>//return Db::table(&#39;test&#39;)-&gt;data($data)-&gt;insert();<br/>//(2)插入同时返回新增主键id<br/>//return Db::table(&#39;test&#39;)-&gt;insertGetId($data);<br/>//(3)插入多条数据<br/>return Db::table(&#39;test&#39;)-&gt;data($dataall)-&gt;insertAll();<br/>}<br/><strong style=\"margin: 0px; padding: 0px;\">//更新数据</strong><br/>public function dbTest7()<br/>{<br/>// return Db::table(&#39;test&#39;)<br/>// -&gt;where(&#39;id&#39;,&#39;=&#39;,4)<br/>// -&gt;update([&#39;name&#39;=&gt;&#39;SamC&#39;,&#39;age&#39;=&gt;&#39;31&#39;]);<br/>//如果where条件是主键，还可以如下使用<br/>return Db::table(&#39;test&#39;)<br/>-&gt;update([&#39;name&#39;=&gt;&#39;SamCheng&#39;,&#39;age&#39;=&gt;&#39;30&#39;,&#39;id&#39;=&gt;4]);<br/>}<br/>//删除操作<br/>public function dbTest8()<br/>{<br/>//return Db::table(&#39;test&#39;)-&gt;delete(6);<br/>//或者<br/>return Db::table(&#39;test&#39;)-&gt;where(&#39;id&#39;,5)-&gt;delete();<br/>}<br/>//mysql原生语句 查询<br/>public function dbTest9()<br/>{<br/>$sql = &quot;select name,age from test where id&gt;2&quot;;<br/>dump(Db::query($sql));<br/>}<br/>//mysql 增、删、改 用Db::execute($sql)<br/>public function dbTest10()<br/>{<br/>//$sql = &quot;update test set name=&#39;samC&#39; where id=4&quot;;<br/>//$sql = &quot;insert test set name=&#39;Yan&#39;,age=&#39;30&#39;&quot;;<br/>$sql = &quot;delete from test where id=4&quot;;<br/>return Db::execute($sql);<br/>}</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(250, 247, 239);\"><br/>}</p><p><br/></p>', '沐兮丨丶', '2020-03-11 08:48:50', 668, 2122, 320, '1', '/uploads/20200311/f4219c5f6b9c4487a52ade2f9ecbde20.png', 0, NULL);
INSERT INTO `blogs` VALUES (2, 'js计算N天后的日期并给日期自动补0', 1, '1', 'js计算N天后的日期并给日期自动补0\n', '<p>js计算N天后的日期并给日期自动补0</p><p><br/></p><p>1.js</p><p><br/></p><p>function date_count(date) {</p><p><span style=\"white-space:pre\">				</span>var date1 = new Date();</p><p><span style=\"white-space:pre\">				</span>var date2 = new Date(date1);</p><p><span style=\"white-space:pre\">				</span>date2.setDate(date1.getDate() + parseInt(date));</p><p><span style=\"white-space:pre\">				</span>var times = date2.getFullYear() + &quot;-&quot; + (date2.getMonth() + 1) + &quot;-&quot; + date2.getDate();</p><p><span style=\"white-space:pre\">				</span>var Year = 0;</p><p><span style=\"white-space:pre\">				</span>var Month = 0;</p><p><span style=\"white-space:pre\">				</span>var Day = 0;</p><p><span style=\"white-space:pre\">				</span>var CurrentDate = &quot;&quot;;</p><p><span style=\"white-space:pre\">				</span>Year = date2.getFullYear();</p><p><span style=\"white-space:pre\">				</span>Month = date2.getMonth() + 1;</p><p><span style=\"white-space:pre\">				</span>Day = date2.getDate();</p><p><span style=\"white-space:pre\">				</span>CurrentDate += Year + &quot;-&quot;;</p><p><span style=\"white-space:pre\">				</span>if(Month &gt;= 10) {</p><p><span style=\"white-space:pre\">					</span>CurrentDate += Month + &quot;-&quot;;</p><p><span style=\"white-space:pre\">				</span>} else {</p><p><span style=\"white-space:pre\">					</span>CurrentDate += &quot;0&quot; + Month + &quot;-&quot;;</p><p><span style=\"white-space:pre\">				</span>}</p><p><span style=\"white-space:pre\">				</span>if(Day &gt;= 10) {</p><p><span style=\"white-space:pre\">					</span>CurrentDate += Day;</p><p><span style=\"white-space:pre\">				</span>} else {</p><p><span style=\"white-space:pre\">					</span>CurrentDate += &quot;0&quot; + Day;</p><p><span style=\"white-space:pre\">				</span>}</p><p><span style=\"white-space:pre\">				</span>alert(CurrentDate) ;</p><p><span style=\"white-space:pre\">			</span>}</p><p><br/></p><p>2.html</p><p><br/></p><p>&lt;button type=&quot;button&quot; οnclick=&quot;date_count(7);&quot;&gt;7天后的时间&lt;/button&gt;</p><p><br/></p>', '沐兮丨丶', '2020-03-11 09:01:48', 666, 111, 253, '111', '/uploads/20200311/51fbce28aae88fdaefae2a4681a349ef.jpg', 0, NULL);
INSERT INTO `blogs` VALUES (3, '数字位数不够，进行前补零的JS最简实现方案', 4, '1', '数字位数不够，进行前补零的JS最简实现方案', '<h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">代码实现</h3><pre class=\"javascript\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; white-space: pre-wrap; overflow-wrap: break-word; color: rgb(51, 51, 51); font-size: 14px; background-color: rgb(255, 255, 255);\">/**\n*&nbsp;自定义函数名：PrefixZero\n*&nbsp;@param&nbsp;num：&nbsp;被操作数\n*&nbsp;@param&nbsp;n：&nbsp;固定的总位数\n*/function&nbsp;PrefixZero(num,&nbsp;n)&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;(Array(n).join(0)&nbsp;+&nbsp;num).slice(-n);\n}</pre><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">具体示例</h3><pre class=\"javascript\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; white-space: pre-wrap; overflow-wrap: break-word; color: rgb(51, 51, 51); font-size: 14px; background-color: rgb(255, 255, 255);\">var&nbsp;myNum&nbsp;=&nbsp;9;var&nbsp;myNum2&nbsp;=&nbsp;12;console.log(&#39;原变量myNum：&#39;+myNum);//9console.log(&#39;处理后myNum：&#39;+PrefixZero(myNum,&nbsp;3));//009\n&nbsp;console.log(&#39;原变量myNum2：&#39;+myNum2);console.log(&#39;处理后myNum2：&#39;+PrefixZero(myNum2,&nbsp;3));//012</pre><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">简要说明如下：</h3><blockquote style=\"margin: 0px; padding: 5px 10px; background: rgb(238, 238, 238); border-top: none; border-right: none; border-bottom: none; border-left: 5px solid rgb(221, 221, 221); border-image: initial; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal;\"><ul style=\"margin-bottom: 1em; margin-left: 25px; padding: 0px;\" class=\" list-paddingleft-2\"><li><p>Array(5) =&gt; 创建了一个长度为5的空数组<br/>console.log(Array(5));// [empty × 5]</p></li></ul></blockquote><blockquote style=\"margin: 0px; padding: 5px 10px; background: rgb(238, 238, 238); border-top: none; border-right: none; border-bottom: none; border-left: 5px solid rgb(221, 221, 221); border-image: initial; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal;\"><ul style=\"margin-bottom: 1em; margin-left: 25px; padding: 0px;\" class=\" list-paddingleft-2\"><li><p>Array(5).join(0) =&gt; 用0拼接将数组转换成字符串<br/>console.log(Array(5).join(0));// 0000</p></li></ul></blockquote><blockquote style=\"margin: 0px; padding: 5px 10px; background: rgb(238, 238, 238); border-top: none; border-right: none; border-bottom: none; border-left: 5px solid rgb(221, 221, 221); border-image: initial; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal;\"><ul style=\"margin-bottom: 1em; margin-left: 25px; padding: 0px;\" class=\" list-paddingleft-2\"><li><p>Array(5).join(0)+91 =&gt; 通过+,实现字符串的拼接<br/>console.log(Array(5).join(0)+91);// 000091</p></li></ul></blockquote><blockquote style=\"margin: 0px; padding: 5px 10px; background: rgb(238, 238, 238); border-top: none; border-right: none; border-bottom: none; border-left: 5px solid rgb(221, 221, 221); border-image: initial; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal;\"><ul style=\"margin-bottom: 1em; margin-left: 25px; padding: 0px;\" class=\" list-paddingleft-2\"><li><p>(Array(5).join(0) + 91).slice(-5) =&gt; slice(startIndex,endIndex)方法，用于截取<br/>参数说明：<br/>参数是起始位置，含头不含尾，<br/>只有一个参数时，表示从该起始位置一直截取到最后。<br/>参数值为负数时，表示从后往前数，如最后一位，索引是-1</p></li></ul></blockquote><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">如此，.slice(-5)表示截取的是从后往前数5位一直到最后<br/>console.log((Array(5).join(0) + 91).slice(-5));// 00091</p><p><br/></p>', '沐兮丨丶', '2020-03-11 08:59:03', 667, 234, 321329, '1', '/uploads/20200311/dc4e413899464908e3af5aac1e0e8ff1.jpeg', 0, NULL);
INSERT INTO `blogs` VALUES (4, 'HTML5的新特性', 26, '1', 'HTML5的新特性有哪些呢？', '<h2 style=\"margin: 10px 0px; padding: 0px; font-size: 21px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">H5 新特性</h2><ol style=\"padding: 0px 0px 0px 40px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><li><p>语义化标签：header、footer、section、nav、aside、article</p></li><li><p>增强型表单：input 的多个 type</p></li><li><p>新增表单元素：datalist、keygen、output</p></li><li><p>新增表单属性：placehoder、required、min 和 max</p></li><li><p>音频视频：audio、video</p></li><li><p>canvas</p></li><li><p>地理定位</p></li><li><p>拖拽</p></li><li><p>本地存储：localStorage - 没有时间限制的数据存储；sessionStorage - 针对一个 session 的数据存储，当用户关闭浏览器窗口后，数据会被删除</p></li><li><p>新事件：onresize、ondrag、onscroll、onmousewheel、onerror、onplay、onpause</p></li><li><p>WebSocket：单个 TCP 连接上进行全双工通讯的协议</p></li></ol><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">语义化标签</h3><table><thead style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">标签</th><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述</th></tr></thead><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">header</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义了文档的头部区域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">footer</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义了文档的尾部区域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">nav</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义文档的导航</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">section</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义文档中的节（section、区段）</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">article</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义页面独立的内容区域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">aside</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义页面的侧边栏内容</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">detailes</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">用于描述文档或文档某个部分的细节</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">summary</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">标签包含 details 元素的标题</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">dialog</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义对话框，比如提示框</td></tr></tbody></table><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">增强型表单</h3><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">HTML5 拥有多个新的表单 Input 输入类型。这些新特性提供了更好的输入控制和验证</p><table><thead style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">input 的 type</th><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述</th></tr></thead><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">color</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">主要用于选取颜色</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">date</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">从一个日期选择器选择一个日期</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">datetime</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">选择一个日期（UTC 时间）</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">email</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">包含 e-mail 地址的输入域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">month</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">选择一个月份</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">number</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">数值的输入域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">range</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">一定范围内数字值的输入域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">search</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">用于搜索域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">tel</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">定义输入电话号码字段</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">time</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">选择一个时间</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">url</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">URL 地址的输入域</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">week</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">选择周和年</td></tr></tbody></table><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">html5 也新增以下表单元素</h3><table><thead style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">表单元素</th><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述</th></tr></thead><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">datalist</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">元素规定输入域的选项列表，使用 input 元素的 list 属性与 datalist 元素的 id 绑定</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">keygen</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">提供一种验证用户的可靠方法，标签规定用于表单的密钥对生成器字段</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">output</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">用于不同类型的输出，比如计算或脚本输出</td></tr></tbody></table><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">html5 新增的表单属性</h3><table><thead style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">表单属性</th><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述</th></tr></thead><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">placehoder</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">简短的提示在用户输入值前会显示在输入域上。即我们常见的输入框默认提示，在用户输入后消失</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">required</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">是一个 boolean 属性。要求填写的输入域不能为空</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">pattern</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述了一个正则表达式用于验证 input 元素的值</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">min 和 max</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">设置元素最小值与最大值</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">step</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">为输入域规定合法的数字间隔</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">height 和 width</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">用于 image 类型的 input 标签的图像高度和宽度</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">autofocus</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">是一个 boolean 属性。规定在页面加载时，域自动地获得焦点</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">multiple</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">是一个 boolean 属性。规定 input 元素中可选择多个值</td></tr></tbody></table><h3 style=\"margin: 10px 0px; padding: 0px; font-size: 16px; line-height: 1.5; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">html5 新事件</h3><table><thead style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\" class=\"firstRow\"><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">事件</th><th style=\"margin: 0px; padding: 8px 14px; background-color: rgb(250, 250, 250); border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">描述</th></tr></thead><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onresize</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当调整窗口大小时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">ondrag</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当拖动元素时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onscroll</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当滚动元素滚动元素的滚动条时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onmousewheel</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当转动鼠标滚轮时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onerror</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当错误发生时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onplay</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当媒介数据将要开始播放时运行脚本</td></tr><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">onpause</td><td style=\"margin: 0px; padding: 8px 14px; border-color: rgb(192, 192, 192); border-collapse: collapse; min-width: 50px;\">当媒介数据暂停时运行脚本</td></tr></tbody></table><ul style=\"margin-left: 30px; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><li><p style=\"margin: 10px auto; padding: 0px;\">块级元素<br/>div、p、h1~h6、ul、ol、dl、li、dd、table、hr、blockquote、address、table、menu、pre，HTML5 新增的 header、section、aside、footer 等</p></li><li><p style=\"margin: 10px auto; padding: 0px;\">行内元素<br/>pan、img、a、lable、input、abbr（缩写）、em（强调）、big、cite（引用）、i（斜体）、q（短引用）、textarea、select、small、sub、sup，strong、u（下划线）、button</p></li></ul><p><br/></p>', '沐兮丨丶', '2020-03-11 08:57:07', 667, 2424, 24243, '1', '/uploads/20200311/ac048ccf90dc9a28970cb20130b4a0b8.jpeg', 0, NULL);
INSERT INTO `blogs` VALUES (5, '前端必须掌握30个CSS3选择器', 3, '1', '也许你已经学会了CSS的三个简单常用的选择器：#ID，.class，标签选择器，可是这些就足够了吗？随着CSS3的到来，作为前端开发者需要掌握下面三十个基本的选择器，这样才可以在平时开发中得心用手。\n\n本文中将综合前端开发中常用的30个CSS3选择器，并且附带了浏览器的支持情况，希望对大家有所帮助。', '<h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">1、*：通用元素选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">*&nbsp;{&nbsp;margin:&nbsp;0;&nbsp;padding:&nbsp;0;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">*选择器是选择页面上的全部元素，上面的代码作用是把全部元素的margin和padding设为0，最基本的<a title=\"清除默认CSS样式注意事项\" href=\"http://js8.in/365.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">清除默认CSS样式</a>方法</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">*选择器也可以应用到子选择器中，例如下面的代码：</p><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">#container&nbsp;*&nbsp;{&nbsp;border:&nbsp;1px&nbsp;solid&nbsp;black;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">这样ID为container 的所有子标签元素都被选中了，并且设置了border。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/star.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE6+</p></li><li><p><a title=\"Firefox\" href=\"http://js8.in/tag/firefox\" style=\"color: rgb(85, 85, 85)\">Firefox</a></p></li><li><p><a title=\"Chrome\" href=\"http://js8.in/tag/chrome\" style=\"color: rgb(85, 85, 85)\">Chrome</a></p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">#ID：ID选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">#container&nbsp;{&nbsp;width:&nbsp;960px;&nbsp;margin:&nbsp;auto;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">ID选择器是CSS中效率最高的选择器，使用的时候要保证ID的唯一性。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/id.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE6+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">.class:类选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">.error&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">类选择器效率低于ID选择器，一个页面可以有多个class，并且class可以放在不同的标签中使用。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/class.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">&nbsp;</p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE6+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X Y：标签组合选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">li&nbsp;a&nbsp;{&nbsp;text-decoration:&nbsp;none;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">标签组合选择器也是常用的选择器。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/descend.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE6+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X：标签选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}&nbsp;ul&nbsp;{&nbsp;margin-left:&nbsp;0;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">如果你只是想要页面中的某个标签样式改变，可以选择使用标签选择器。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/tagName.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE6+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X:visited and X:link</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a:link&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}&nbsp;a:visted&nbsp;{&nbsp;color:&nbsp;purple;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">伪类选择器，最常用的为A标签</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/links.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X + Y：毗邻元素选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">ul&nbsp;+&nbsp;p&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">毗邻元素选择器，匹配的是所有紧随X元素之后的同级元素Y</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/adjacent.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X &gt; Y：子元素选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">div#container&nbsp;&gt;&nbsp;ul&nbsp;{&nbsp;border:&nbsp;1px&nbsp;solid&nbsp;black;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">匹配#container下的所有子元素。<br/>关于<code>X&gt;Y</code>和<code>X Y</code>的区别请看下面的html实例：</p><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">&lt;div&nbsp;id=&quot;container&quot;&gt;&nbsp;&lt;ul&gt;&nbsp;&lt;li&gt;&nbsp;List&nbsp;Item&nbsp;&lt;ul&gt;&nbsp;&lt;li&gt;&nbsp;Child&nbsp;&lt;/li&gt;&nbsp;&lt;/ul&gt;&nbsp;&lt;/li&gt;&nbsp;&lt;li&gt;&nbsp;List&nbsp;Item&nbsp;&lt;/li&gt;&nbsp;&lt;li&gt;&nbsp;List&nbsp;Item&nbsp;&lt;/li&gt;&nbsp;&lt;li&gt;&nbsp;List&nbsp;Item&nbsp;&lt;/li&gt;&nbsp;&lt;/ul&gt;&nbsp;&lt;/div&gt;</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">选择器<code>#container &gt; ul</code>只会匹配到第一个UL，也就是#container的子元素UL，而不是li里面的ul，但是<code>div ul</code>则可以匹配到所有DIV里面的ul。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/childcombinator.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X ~ Y：</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">ul&nbsp;~&nbsp;p&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">匹配任何在X元素之后的同级P元素。也就是选择了UL之后的同级所有的元素。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/generalcombinator.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[title]：属性选择器</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[title]&nbsp;{&nbsp;color:&nbsp;green;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">匹配具有某属性的标签，例如实例中是匹配具有title属性的a标签。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[href=&quot;foo&quot;]</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[href=&quot;http://js8.in&quot;]&nbsp;{&nbsp;color:&nbsp;#1f6053;&nbsp;/*&nbsp;nettuts&nbsp;green&nbsp;*/&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">也属于属性选择器，匹配属性中为某个值的标签。例如实例中匹配的为<code>href=&quot;http://js8.in&quot;</code>的a标签，而其他链接的a标签不选择。</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes2.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[href*=&quot;nettuts&quot;]</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[href*=&quot;tuts&quot;]&nbsp;{&nbsp;color:&nbsp;#1f6053;&nbsp;/*&nbsp;nettuts&nbsp;green&nbsp;*/&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">属于属性选择器，匹配href中所有含有tuts的标签。正则匹配</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes3.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[href^=&quot;http&quot;]</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[href^=&quot;http&quot;]&nbsp;{&nbsp;background:&nbsp;url(path/to/external/icon.png)&nbsp;no-repeat;&nbsp;padding-left:&nbsp;10px;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">与上面的属相选择标签类似，但是匹配的以http开头的A标签，正则匹配</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes4.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[href$=&quot;.jpg&quot;]</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[href$=&quot;.jpg&quot;]&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">匹配属性中以.jpg结尾的标签，正则匹配，也是属性选择器的一种</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes5.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[data-*=&quot;foo&quot;]</h3><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">如果你要匹配所有的图片链接，你可以通过下面的CSS来实现：</p><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[href$=&quot;.jpg&quot;],&nbsp;a[href$=&quot;.jpeg&quot;],&nbsp;a[href$=&quot;.png&quot;],&nbsp;a[href$=&quot;.gif&quot;]&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">但是如果我们给a标签添加一个data-filetype属性，我们就可以使用下面的CSS来快速的选择我们需要匹配的标签了。</p><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">&lt;a&nbsp;href=&quot;path/to/image.jpg&quot;&nbsp;data-filetype=&quot;image&quot;&gt;&nbsp;Image&nbsp;Link&nbsp;&lt;/a&gt;&nbsp;&lt;/html&gt;&nbsp;&lt;pre&gt;a[data-filetype=&quot;image&quot;]&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes6.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"padding-left: 40px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\" class=\" list-paddingleft-2\"><li><p>IE7+</p></li><li><p>Firefox</p></li><li><p>Chrome</p></li><li><p>Safari</p></li><li><p>Opera</p></li></ol><h3 style=\"font-size: 17px;line-height: 25px;background: rgb(43, 102, 149);border-radius: 6px;box-shadow: rgb(95, 90, 75) 0px 0px 0px 1px, rgba(10, 10, 0, 0.5) 1px 1px 6px 1px;color: rgb(255, 255, 255);font-family: 微软雅黑, 宋体, 黑体, Arial;height: 25px;padding: 8px 0px 5px 5px;text-shadow: rgb(34, 34, 34) 2px 2px 3px;white-space: normal;margin: 18px 0px !important\">X[foo~=&quot;bar&quot;]</h3><pre style=\"margin-top: 0px;margin-bottom: 0px;white-space: pre-wrap;overflow-wrap: break-word;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 13.3333px;line-height: normal;font-family: Arial\">a[data-info~=&quot;external&quot;]&nbsp;{&nbsp;color:&nbsp;red;&nbsp;}&nbsp;&nbsp;&nbsp;a[data-info~=&quot;image&quot;]&nbsp;{&nbsp;border:&nbsp;1px&nbsp;solid&nbsp;black;&nbsp;}</pre><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">匹配属性中具有多个空格分隔的值、其中一个值等于“bar”的X元素，例如下面的例子：</p><p style=\"margin: 10px auto;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\"><a href=\"http://d2o0t5hpnwv4c1.cloudfront.net/840_cssSelectors/selectors/attributes7.html\" target=\"_blank\" style=\"color: rgb(85, 85, 85)\">查看演示</a></p><h5 style=\"font-size: 12px;margin: 10px 0px;font-family: 微软雅黑, PTSans, Arial, sans-serif;white-space: normal\">兼容性</h5><ol style=\"paddi